import { Button, Input, Layout, Space, Image, Table, Tooltip, Modal } from 'antd';
import { PlusOutlined, DeleteOutlined, EditOutlined, ExclamationCircleOutlined } from '@ant-design/icons';
import React, { useEffect, useState } from 'react';
import { GiftType } from 'interface';
import { AddGiftModal } from './addGiftModal';
import { RootState, useAppDispatch } from 'store/store';
import { actionDeleteGift, actionGetGiftList } from 'store/gifts/slice';
import { useSelector } from 'react-redux';
import { get } from 'lodash';
const { confirm } = Modal;

export function Gifts(): JSX.Element {
    const dispatch = useAppDispatch();
    const [showModal, setShowModal] = useState(false);
    const [editMode, setEditMode] = useState(false);
    const [editingGift, setEditingGift] = useState<GiftType | null>(null);

    const giftList = useSelector((state:RootState) => state.giftReducer.giftList);
    const getGiftListStatus = useSelector((state:RootState) => state.giftReducer.getGiftListStatus);


    useEffect(()=>{
        dispatch(actionGetGiftList({per_page:20}));
    },[dispatch])


    function handleUpdate(record:GiftType){
        setEditingGift(record);
        setShowModal(true);
        setEditMode(true)
    }

    function handleDelete(ID:number){
        confirm({
            title: "Xác nhận xoá quà tặng?",
            icon: <ExclamationCircleOutlined />,
            onOk() {
                dispatch(actionDeleteGift(ID)).finally(() => {
                    dispatch(actionGetGiftList({ per_page: 20 }))
                })
            }
        })
    }

    const gift_cols:any[] = [
        {
            title:"Ảnh",
            key:"image",
            dataIndex:"",
            render: function ImageCol(text:string, record:GiftType):JSX.Element {
                return(
                    <Image
                    width={80}
                    height={80}
                    src={get(record,"photo.url","error")}
                    fallback="data:image/png;base64,"
                  />
                )
            }
        },
        {
            title:"Tên qùa tặng",
            key:"name",
            dataIndex:"name",
        },
        {
            title:"Loại",
            key:"type",
            dataIndex:"type",
        },
        {
            title:"Số lượng",
            key:"quantity",
            dataIndex:"quantity",
        },
        {
            title:"Actions",
            key:"action",
            dataIndex:"",
            render: function actionCol(text:string, record:GiftType):JSX.Element {
                return(
                  <Space>
                      <Tooltip title="Xoá">
                          <Button type="link" danger icon={<DeleteOutlined />} onClick={()=>handleDelete(get(record,"id",0))}/>
                          </Tooltip>
                      <Tooltip title="Sửa">
                          <Button type="link" icon={<EditOutlined />} onClick={() => handleUpdate(record)}/>
                      </Tooltip>
                  </Space>
                )
            }
        },

    ]

    return (
        <Layout.Content>
            <AddGiftModal show={showModal} setShow={setShowModal} giftInfo={editingGift} editMode={editMode} setEditMode={setEditMode} />
            <Space style={{marginBottom:20}}>
                <Input style={{ width: 300 }} placeholder="Tìm theo tên" />
                <Button type="primary" icon={<PlusOutlined />} onClick={() => {
                    setEditMode(false);
                    setShowModal(true);
                }}>Thêm quà tặng</Button>
            </Space>
            <Table columns={gift_cols} dataSource={get(giftList,"data",[])} loading={getGiftListStatus === "loading" ? true : false}/>
            
        </Layout.Content>
    )
}


